import React, { useState } from "react";
import "bootstrap-icons/font/bootstrap-icons.css";

const Sidebar = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  // Toggle sidebar visibility
  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <div className="container">
      {/* Burger button outside the sidebar */}
      <button onClick={toggleSidebar} className="burger-icon">
        <i className="bi bi-list"></i>
      </button>

      {/* Sidebar */}
      <div className={`sidebar ${isSidebarOpen ? "open" : "closed"}`}>
        <p>{isSidebarOpen && "Modules"}</p>
        <ul>
          <li>
            <a href="#">
              <i className="bi bi-house-door"></i>
              {isSidebarOpen && "Dashboard"}
            </a>
          </li>
          <li>
            <a href="#">
              <i className="bi bi-credit-card"></i>
              {isSidebarOpen && "Payment"}
            </a>
          </li>
          <li>
            <a href="#">
              <i className="bi bi-box"></i>
              {isSidebarOpen && "Products"}
            </a>
          </li>
          <li>
            <a href="#">
              <i className="bi bi-bar-chart-line"></i>
              {isSidebarOpen && "Reports"}
            </a>
          </li>
          <li>
            <a href="#">
              <i className="bi bi-people"></i>
              {isSidebarOpen && "Students"}
            </a>
          </li>
          <li>
            <a href="#">
              <i className="bi bi-person-badge"></i>
              {isSidebarOpen && "Employees"}
            </a>
          </li>
          <li>
            <a href="#">
              <i className="bi bi-cash"></i>
              {isSidebarOpen && "Other Payment"}
            </a>
          </li>
          <li>
            <a href="#">
              <i className="bi bi-box-arrow-right"></i>
              {isSidebarOpen && "Logout"}
            </a>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Sidebar;
